package ch03;

public class BitOperator2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 5;
		int num2 = 4;
		
		System.out.println( num1<<1 );
		System.out.println( num1<<3 );
		System.out.println( num2>>1 );
		System.out.println( num2>>2 );
		System.out.println( num2>>>1 );
		
	}

}
